formulas.expression:
/*
  1. New Business
      1-a. With Connector
      1-b. Lead was from SFDC or influenced by SFDC
  2. Upsell/Downsell
      2-a. With Connector
      2-b. Already owes 20% ARR, and Opp MRR is not 0
  3. Renewal
      3-a. Already owes 20% ARR, and ARR is changed
      3-b. Already owes 20% Connector, and Connector Sales Price is changed
*/

IF(
  /* - - - - - - - - - - New Business - - - - - - - - - - */
  CONTAINS(TEXT({!varOpportunityRecord.Type}), "New Business"), 
  OR(
    {!varOpportunityRecord.of_SFMC_Connector__c} <> 0,
    {!varOpportunityRecord.of_SFCC_Connector__c} <> 0,
    {!varOpportunityRecord.of_SFLWC_Connector__c} <> 0,
    AND(
      NOT(ISBLANK({!varOpportunityRecord.OEM__c})),
      CONTAINS({!varOpportunityRecord.OEM__r.Name}, "Salesforce")
    ),
    ISPICKVAL({!varOpportunityRecord.LeadSource}, "AppExchange - Marketing Cloud"),
    ISPICKVAL({!varOpportunityRecord.LeadSource}, "AppExchange - Commerce Cloud"),
    ISPICKVAL({!varOpportunityRecord.LeadSource}, "AppExchange - LWC")
  ),

IF(
  /* - - - - - - - - - - Upsell/Downsell - - - - - - - - - - */
  ISPICKVAL({!varOpportunityRecord.Type}, "Existing Business-Upsell"),
  OR(
    {!varOpportunityRecord.of_SFMC_Connector__c} <> 0,
    {!varOpportunityRecord.of_SFCC_Connector__c} <> 0,
     {!varOpportunityRecord.of_SFLWC_Connector__c} <> 0,

    AND(
      ISPICKVAL({!varOpportunityRecord.Account.SFMC_Commission__c}, "20% of ARR"),   
      {!varOpportunityRecord.MRR__c} <> 0                                                
    ),
    AND(
      ISPICKVAL({!varOpportunityRecord.Account.SFCC_Commission__c}, "20% of ARR"),
      {!varOpportunityRecord.MRR__c} <> 0
    ),
    AND(
      ISPICKVAL({!varOpportunityRecord.Account.SFLWC_Commission__c}, "20% of ARR"),
      {!varOpportunityRecord.MRR__c} <> 0

     ),
     AND(
      ISPICKVAL({!varOpportunityRecord.Account.Other_Commission__c}, "20% of ARR"),
      {!varOpportunityRecord.MRR__c} <> 0

     )
  ),

IF(
  /* - - - - - - - - - - Renewal - - - - - - - - - - */
  ISPICKVAL({!varOpportunityRecord.Type}, "Existing Business-Renewal"),
  OR(
    AND(
      ISPICKVAL({!varOpportunityRecord.Account.SFMC_Commission__c}, "20% of ARR"),
      {!varOpportunityRecord.MRR__c} <> {!varOpportunityRecord.Original_Renewal_Amount__c}  
    ),

    AND(
      ISPICKVAL({!varOpportunityRecord.Account.SFCC_Commission__c}, "20% of ARR"),
      {!varOpportunityRecord.MRR__c} <> {!varOpportunityRecord.Original_Renewal_Amount__c}
    ),

    AND(
      ISPICKVAL({!varOpportunityRecord.Account.SFLWC_Commission__c}, "20% of ARR"),
      {!varOpportunityRecord.MRR__c} <> {!varOpportunityRecord.Original_Renewal_Amount__c}
    ),

    AND(
      ISPICKVAL({!varOpportunityRecord.Account.Other_Commission__c}, "20% of ARR"),
      {!varOpportunityRecord.MRR__c} <> {!varOpportunityRecord.Original_Renewal_Amount__c}
    ),

    AND(
      ISPICKVAL({!varOpportunityRecord.Account.SFMC_Commission__c}, "20% of Connector"),
      {!varOpportunityRecord.SFMC_Connector_Sales_Price__c} <>
        {!varOpportunityRecord.Account.SFMC_Connector_Sales_Price__c}
    ),

    AND(
      ISPICKVAL({!varOpportunityRecord.Account.SFCC_Commission__c}, "20% of Connector"),
      {!varOpportunityRecord.SFCC_Connector_Sales_Price__c} <>
        {!varOpportunityRecord.Account.SFCC_Connector_Sales_Price__c}
    ),
    AND(
      ISPICKVAL({!varOpportunityRecord.Account.SFLWC_Commission__c}, "20% of Connector"),
      {!varOpportunityRecord.SFLWC_Connector_Sales_Price__c} <>
        {!varOpportunityRecord.Account.SFLWC_Connector_Sales_Price__c}
    )
  ),
  FALSE
)
)
)
