ValidationRule.errorConditionFormula:
ISCHANGED(Price_Uplift_On_Renewal__c) &&
(
/* Price uplift is 'Yes' and Price uplift percentage is blank or zero */
(
TEXT(Price_Uplift_On_Renewal__c) = 'Yes' &&
(ISBLANK(Price_Uplift_on_Renewal_percentage__c) || Price_Uplift_on_Renewal_percentage__c <=0)
) ||
/* Price uplift is 'No' or blank and Price uplift percentage has a value */
(TEXT(Price_Uplift_On_Renewal__c) != 'Yes' &&
NOT(ISBLANK(Price_Uplift_on_Renewal_percentage__c)))
)
