ValidationRule.errorConditionFormula:
AND(
NOT($Profile.Name = 'System Administrator'),
ISCHANGED(Portal_ID__c),
Portal_ID__c !=null,
OR(
/* Check Portal ID when Platform is Bynder*/
AND(TEXT(Platform__c) ='Bynder',
OR(LEN(Portal_ID__c) <> 35,
FIND('-',Portal_ID__c) <> 9
)
),
/* Check Portal ID when Platform is Webdam*/
AND(TEXT(Platform__c) ='Webdam',
OR(LEN(Portal_ID__c) <> 5,
!REGEX(Portal_ID__c,"[0-9]*")
)
)
)
)
