,
LEN( IF( Power_User__c , "Power User; ", "") & IF( Executive_Sponsor__c , "Champion; ", "") & IF( Support_contact__c , "Support Contact; ", "") & IF( Decision_Maker__c , "Decision Maker; ", "") & IF( Project_Owner__c , "Project Owner; ", "") & IF( Legal__c , "Legal; ", "") & IF( IT_Contact__c , "IT Contact; ", "") & IF( Procurement__c , "Procurement; ", "") & IF( Stakeholder__c , "Stakeholder; ", "") & IF( Finance__c , "Finance; ", "") ) - 2
