ValidationRule.errorConditionFormula:
AND(
    NOT($Profile.Name = "Automated Process"),

    /* When a Lead is converted into Opportunity */
    ISCHANGED(Status),
    ISPICKVAL(Status,"Converted"),
    NOT(ISBLANK(ConvertedOpportunityId)),

    /* when Module Opportunity is balnk */
    ISBLANK(Module_Opportunity__c)
)
