ValidationRule.errorConditionFormula:
AND(
/* Exempting AutoProcess */
    NOT($Profile.Name = "Automated Process"),

    OR(
    /*On Opportunity creation */
        AND(ISNEW(), 
            NOT(ISPICKVAL(Technical_Win_Assessment__c,'')),
            NOT(ISPICKVAL(Technical_Win_Assessment__c,'Meets all requirements')),
            Technical_Win_Assessment_Details__c = null), 

    /*On Opportunity update */
        AND(ISCHANGED(Technical_Win_Assessment__c),
            NOT(ISPICKVAL(Technical_Win_Assessment__c,'Meets all requirements')),
            NOT(ISCHANGED(Technical_Win_Assessment_Details__c)))
    )
)
