CustomField.formula:
IF(
    /* Contraction can be shown only on Recurring Product under Won Opp */
    AND(
        ISPICKVAL(Opportunity.StageName, "Closed Won"),
        For_Renewal__c = TRUE
    ),
    IF(
        /* Negative ARR on Upsell or Expansion Portal */
        AND(
            ISPICKVAL(Opportunity.Type, "Existing Business-Upsell") ||
            ISPICKVAL(Opportunity.Type, "New Business - Expansion Portal"),
            Total_Sales_Price_ARR__c < 0
        ),
        - Total_Sales_Price_ARR__c,
        /* Contraction on Platform Transfer or OEM>Direct Transfer */
        IF(
            AND(
                /* Contraction recognised only when ARR before Transfer is specified
                and the line item ARR is less than Previous OEM ARR for the line item */
                ISPICKVAL(Opportunity.Type, "Existing Business - Internal Transfer")||
                ISPICKVAL(Opportunity.Type, "Existing Business - Platform Transfer"),
                Opportunity.Original_Renewal_ARR__c > 0,
                Previous_OEM_ARR__c != 0,
                Total_Sales_Price_ARR__c < Previous_OEM_ARR__c
            ),
            Previous_OEM_ARR__c - Total_Sales_Price_ARR__c,
            NULL
        )
    ),
    NULL
)
