CustomField.formula:
IF(
	AND(
		NOT(Order_Type__c = "Pre-Order"),
		Total_Order_Value__c > 0,
		OR(
			/* - - - - - Orders on Renewal: 60 days before Order Start Date - - - - - */
			AND(
				EffectiveDate - 60 <=TODAY(),
				ISPICKVAL(Status, "Draft"),
				ISPICKVAL(Opportunity.Type,"Existing Business-Renewal")
			),
			/* - - - - - Orders on New Business (Not the first order of the contract): 60 days before Order Start Date - - - - -*/
			AND(
				EffectiveDate - 60 <=TODAY(),
				ISPICKVAL(Status, "Draft"),
				NOT(Opportunity.Subscription_Start_Date__c = EffectiveDate),
				OR(
					ISPICKVAL(Opportunity.Type,"New Business - Expansion Portal"),
                                        ISPICKVAL(Opportunity.Type,"New Business-Enterprise Cloud"),
					ISPICKVAL(Opportunity.Type,"Referral")
				)
			),

			/* - - - - - Orders on New Business (First order of the contract): 60 days before Order Start Date - - - - - */
			AND(
				EffectiveDate - 60 <=TODAY(),
				ISPICKVAL(Status, "Draft"),
				Opportunity.Subscription_Start_Date__c=EffectiveDate,
				OR(
					ISPICKVAL(Opportunity.Type,"New Business - Expansion Portal"),
					ISPICKVAL(Opportunity.Type,"New Business-Enterprise Cloud"),
					ISPICKVAL(Opportunity.Type,"Referral")
				)
			),
			/* - - - - - Orders on Transfer: 60 days before Order Start Date - - - - -*/
			AND(
				EffectiveDate - 60 <=TODAY(),
				ISPICKVAL(Status, "Draft"),
				OR(
					ISPICKVAL(Opportunity.Type,"Existing Business - Internal Transfer"),
					ISPICKVAL(Opportunity.Type,"Existing Business - Platform Transfer")
				)
			),
			/* - - - - - Upsell & Prof. Service: 60 days before Order Start Date - - - - - */
			AND(
				EffectiveDate - 60 <=TODAY(),
				ISPICKVAL(Status, "Draft"),
				OR(
					ISPICKVAL(Opportunity.Type,"Existing Business-Professional Services"),
					ISPICKVAL(Opportunity.Type,"Existing Business-Upsell")
				)
			)
		)
	),
	TRUE,
	FALSE
)
